INSERT INTO `jawaban_tugas` VALUES   ('32','37','7','<p>1. AC berfungsi sebagai pendingin ruangan ini menjadi yang panas</p>\r\n<p>3 cara kerja AC adalah menyerap panas dari udara di dalam ruangan kemudian melepaskan panas tersebut di luar ruangan&nbsp;</p>\r\n<p>4 bahan Pendingan berupa fluida yg di gunakan untuk menyerap kalor melelai&nbsp;</p>\r\n<p>Perubahan puasa cair ke gas dan membuang kalor melalui embun</p>\r\n<p>&nbsp;</p>',NULL,NULL,'2022-10-17 09:48:34',NULL,NULL);
